<?php

function btnTemplate($btnText, $btnType = null, $btnSize = null)
{
    $btnClass[] = 'btn';
    if (!empty($btnType)) {
        $btnClass[] = "btn-" . $btnType;
    }
    if (!empty($btnSize)) {
        $btnClass[] = "btn-" . $btnSize;
    }
    $btnClassString = implode(' ', $btnClass);

    echo "<div><button type='button' class='" . $btnClassString . " shadow'>$btnText</button></div>";

}

